import React,  { useState } from "react";

function CreateTodo(props){
    const[todoItem, setToDoitem] = useState({
        desc: "",
        responsible: "",
        priority: "",
        completed: false
    });

    function onChangeDesc(event){
        const {value} = event.target;
        setToDoitem((prevInfo) => {
            return{
            ...prevInfo,
            desc: value
            };
        });
    }

    function onChangeResponsible(event){
        const {value} = event.target;
        setToDoitem((prevInfo) => {
            return{
            ...prevInfo,
            responsible: value
            };
        });
    }

    function onChangePriority(event){
        const {value} = event.target;
        setToDoitem((prevInfo) => {
            return{
            ...prevInfo,
            priority: value
            };
        });
    }

    function onSubmit(event){
        event.preventDefault();

        console.log("Form submitted");
        console.log("Desc: " + todoItem.desc);
        console.log("Resp: " + todoItem.responsible);
        console.log("Priority: " + todoItem.priority);
        console.log("Completed: " + todoItem.completed);

        const newTodo ={
            desc: todoItem.desc,
            responsible: todoItem.responsible,
            priority: todoItem.priority,
            completed: todoItem.completed
        }

        setToDoitem({
            desc: "",
            responsible: "",
            priority: "",
            completed: false
        });
    }

    return(
        <div>
            <h3>Create New Todo</h3>
            <form onSubmit={onSubmit}>
                <div className="form-group">
                    <label>Description: </label>
                    <input type="text"
                        className="form-control"
                        value={todoItem.desc}
                        onChange={onChangeDesc}
                    />
                </div>

                <div className="form-group">
                    <label>Responsible: </label>
                    <input type="text"
                        className="form-control"
                        value={todoItem.responsible}
                        onChange={onChangeResponsible}
                    />
                </div>


                <div className="form-group">
                    <div className="form-check form-check-inline">
                        <input className="form-check-input"
                            type="radio"
                            name="proprityOptions"
                            id="priorityLow"
                            value="Low"
                            checked={todoItem.priority === "Low"}
                            onChange={onChangePriority}
                        />
                        <label className="form-check-label">Low</label>
                        </div>

                    <div className="form-check form-check-inline">
                        <input className="form-check-input"
                            type="radio"
                            name="proprityOptions"
                            id="priorityMedium"
                            value="Medium"
                            checked={todoItem.priority === "Medium"}
                            onChange={onChangePriority}
                        />
                        <label className="form-check-label">Medium</label>
                    </div>

                    <div className="form-check form-check-inline">
                        <input className="form-check-input"
                            type="radio"
                            name="proprityOptions"
                            id="priorityHigh"
                            value="High"
                            checked={todoItem.priority === "High"}
                            onChange={onChangePriority}
                        />
                        <label className="form-check-label">High</label>


                    </div>
                </div>

                <div className="form-group">
                    <input type="submit" value="Create Todo" className="btn btn-primary" />

                </div>


            </form>
        </div>
    )
}

export default CreateTodo;
