import React, {useState} from "react";

function Register(){
  const[registerItem, setRegisterItem] = useState({
    username: "",
    email: "",
    password: "",
    passwordRepeat: ""
  });

 function onChangeUsername(event){
   const {value} = event.target;
   setRegisterItem((prevInfo) => {
      return{
      ...prevInfo,
      username: value
      };
    });
  }

  function onChangeEmail(event){
    const {value} = event.target;
    setRegisterItem((prevInfo) => {
       return{
       ...prevInfo,
       email: value
       };
     });
   }

   function onChangePassword(event){
     const {value} = event.target;
     setRegisterItem((prevInfo) => {
        return{
        ...prevInfo,
        password: value
        };
      });
    }

    function onChangePasswordRepeat(event){
      const {value} = event.target;
      setRegisterItem((prevInfo) => {
         return{
         ...prevInfo,
         passwordRepeat: value
         };
       });
     }


  return(
    <div>
    <div className = "div1" >
      <div className = "ContainerCenter" >
      <h3 className="mainSubTitle">Start your journey</h3>
      <p className="bodyTextLight">Already Registered?</p>

      <form action ="/register" method="POST">
        <div className="form-group">
            <input type="username"
                className="form-control"
                name="username"
                placeholder="username"
                value={registerItem.username}
                onChange={onChangeUsername}
            />
        </div>

        <div className="form-group">
            <input type="email"
                className="form-control"
                name="email"
                placeholder="email"
                value={registerItem.email}
                onChange={onChangeEmail}
            />
        </div>

        <div className="form-group">
            <input type="password"
                className="form-control"
                name="password"
                placeholder="password"
                value={registerItem.password}
                onChange={onChangePassword}
            />
        </div>

        <div className="form-group">
            <input type="password"
                className="form-control"
                name="password-repeat"
                placeholder="repeat password"
                value={registerItem.passwordRepeat}
                onChange={onChangePasswordRepeat}
            />
        </div>

        <div className="form-group">
          <input type="submit" value="Register" className="btn btn-light" />
        </div>

      </form>

      </div>
    </div>

    <div className = "divTownship" > < /div>

    </div>
  );
}


export default Register;
